import React, {useState, useMemo} from 'react';
import Auth from 'pages/auth/auth';
import {useSignInUser} from 'hooks/use-service/use-service';


const SignInAuthContainer = ({match}) => {
  const [email, setEmail] = useState(``);
  const [password, setPassword] = useState(``);
  const userData = useMemo(() => ({
    user: {email, password},
  }), [email, password]);

  const [{isLoading}, doRequest] = useSignInUser(userData);

  const handleFormSubmit = (evt) => {
    evt.preventDefault();
    doRequest();
  };

  return (
    <Auth
      path={match.path}
      username={``}
      email={email}
      password={password}
      isLoading={isLoading}
      setUsername={null}
      setEmail={setEmail}
      setPassword={setPassword}
      onFormSubmit={handleFormSubmit} />);
};

export default SignInAuthContainer;