import {useCallback} from 'react';
import MediumCloneService from 'services/medium-clone-service/medium-clone-service';
import useRequest from 'hooks/use-request/use-request';

const mediumCloneService = new MediumCloneService();

const useSignUpUser = (registerData) => {
  const request = useCallback(() => mediumCloneService.signUpUser(registerData), [registerData]);

  return useRequest(request);
};

const useSignInUser = (userData) => {
  const request = useCallback(() => mediumCloneService.signInUser(userData), [userData]);

  return useRequest(request);
};

export {useSignUpUser, useSignInUser};