import React from 'react';
import {Route, Switch} from 'react-router-dom';

import GlobalFeed from 'pages/global-feed/global-feed';
import Article from 'pages/article/article';
import SignInAuthContainer from 'containers/sign-in-auth-container/sign-in-auth-container';
import SignUpAuthContainer from 'containers/sign-up-auth-container/sign-up-auth-container';

export default () => {

  return (
    <Switch>
      <Route exact path="/" component={GlobalFeed} />
      <Route path="/login" component={SignInAuthContainer} />
      <Route path="/register" component={SignUpAuthContainer} />
      <Route path="/articles/:slug" component={Article} />
    </Switch>
  );
}